vector<ll> prime_factorization(ll n)
{
	vector<ll> cnt_pf(sqrt(n), 0);
	FOR(i, 1, SZ(cnt_pf))
	{
		while (n % (i + 1) == 0)
		{
			cnt_pf[i]++;
			n /= (i + 1);
		}
		if (n == 1)
			break;
	}
	if (n != 1)
	{
		cnt_pf[0] = n;
	}
	return cnt_pf;
}

