vector<ll> cumulative_sum(vector<ll> a)
{
	vector<ll> v(SZ(a) + 1);
	v[0] = 0;
	REP(i, SZ(a))
	{
		v[i + 1] = v[i] + a[i];
	}
	return v;
}

//  a^n
ll iterativePower(ll a, ll n)
{
	ll res = 1;
	while (n > 0)
	{
		if (n & 1)
			res = (res * a) % MOD;
		a = a * a % MOD;
		n >>= 1;
	}
	return res;
}

// MOD
vector<ll> MODInverse(ll n, ll factN)
{
	vector<ll> res(n + 1);
	res[n] = iterativePower(factN, MOD - 2);
	REPR(i, n - 1)
	{
		res[i] = res[i + 1] * (i + 1) % MOD;
	}
	return res;
}

