void factorialFunc(ll n)
{
	factorial.pb(1);
	FOR(i, 1, n + 1)
	{
		factorial.pb(factorial[i - 1] * i % MOD);
	}
	vector<ll> fact;
	fact = MODInverse(n, factorial[n]);

	REP(i, n + 1)
	{
		factorialInverse.pb(fact[i]);
	}
}

