vector<ll> makeDivisors(ll n)
{
	vector<ll> divisors;
	for (ll i = 1; i * i <= n; i++)
	{
		if (n % i == 0)
		{
			divisors.pb(i);
			if (i != n / i)
			{
				divisors.pb(n / i);
			}
		}
	}
	sort(ALL(divisors));

	return divisors;
}

// 
ll shakutori(vector<ll>& v, ll x)
{
	ll res = 0;
	ll n = SZ(v);

	ll sum = 0;
	ll right = 0;

	REP(left, n)
	{

		while (right < n && sum + v[right] <= x)
		{
			sum += v[right];
			right++;
		}
		res += (right - left);

		if (right == left)
			right++;
		else
			sum -= v[left];
	}

	return res;
}


