					que.insert(que.begin() + table[j], que.at(j));
					que.erase(que.begin() + j);
					que.insert(que.begin() + j, tmp);
					chk[j] = chk[table[j]] = false;
				}
			}
		}
		else {
			ll tmp = que.back();
			que.pop_back();
			que.push_front(tmp);
		}
	}

	REP(i, n3) {
