void build(int x,int v,int fl,int pw){
	if(fl==n) return ;
	for(int i=0;i<3;i++) ch[x][i]=fl+1<n?++cnt:v+i*pw,build(fl+1<n?cnt:v+i*pw,v+i*pw,fl+1,pw*3);
}

void dfs(int x,int v,int fl,int pw){
	if(tg[x]) swap(ch[x][1],ch[x][2]);
	if(fl==n){
		ans[x]=v;
		return ;
	}
	for(int i=0;i<3;i++) tg[ch[x][i]]^=tg[x],dfs(ch[x][i],v+i*pw,fl+1,pw*3); tg[x]=0;
}

void upr(int x,int fl){
	if(tg[x]) swap(ch[x][1],ch[x][2]);
	if(fl==n) return ;
	for(int i=0;i<3;i++) tg[ch[x][i]]^=tg[x]; tg[x]=0;
	swap(ch[x][1],ch[x][2]);
	swap(ch[x][0],ch[x][1]);
	upr(ch[x][0],fl+1);
}

void ups(){
	tg[m]^=1;
}

