void update(int q,int &last,int &next){
    if(next!=0&&last%2!=q%2)next=3-next;
    last=q;
}


pvv createbit(int N){
    if(N==0){
        pvv send;
        send.first={0};
        send.second=vector<int>(T.size(),0);
        return send;
    }
    pvv receve = createbit(N-1);
    pvv send;
    int pot = (int)receve.first.size();
    send.first=vector<int>(pot*3,0);
    send.second=vector<int>(T.size());
    vector<int>next(pot*3);
    int q=0;
    vector<int>last(pot*3,0);
    for(int i=0;i<3;i++)for(int x=pot*i;x<pot*(i+1);x+=1)next[x]=i;



    for(int i=0;i<T.size();i++){
        if(T[i]=='S'){
            q++;
            continue;
        }

        int k=receve.second[i];

        for(int x=k;x<pot*3;x+=pot){
            update(q, last[x], next[x]);

            if(next[x]==2)send.second[i]=x;
        }

        for(int x=k;x<=pot*3;x+=pot)next[x]=(next[x]+1)%3;

    }

    for(int i=0;i<pot*3;i++){
        update(q, last[i], next[i]);
        send.first[i]=receve.first[i%pot]+next[i]*pot;
    }

    return send;

}

