struct Trie {
	int rt, cnt, son[N][3], id[N];
	bool tag[N];

	void build(int &u, int d, int v) {
		u = ++cnt, tag[u] = 0;
		if (d == n) {
			id[u] = v;
			return;
		}
		for (int i = 0; i < 3; ++i) {
			build(son[u][i], d + 1, v + i * pw[d]);
		}
	}

	void rev(int u) {
		std::swap(son[u][1], son[u][2]);
		tag[u] ^= 1;
	}

	void down(int u) {
		if (tag[u]) {
			rev(son[u][0]), rev(son[u][1]), rev(son[u][2]);
			tag[u] = 0;
		}
	}

	void add(int u) {
		if (!son[u][0]) {
			return;
		}
		down(u);
		int t = son[u][2];
		son[u][2] = son[u][1];
		son[u][1] = son[u][0];
		son[u][0] = t;
		add(son[u][0]);
	}

	void getans(int u, int d, int v) {
		if (d == n) {
			ans[id[u]] = v;
			return;
		}
		down(u);
		for (int i = 0; i < 3; ++i) {
			getans(son[u][i], d + 1, v + i * pw[d]);
		}
	}
} T;

