void solve() {
    pw[0] = 1;
    for(int i = 1 ; i <= 12 ; ++i) pw[i] = pw[i-1]*3;
    cin >> n;
    cin >> s;
    f.push_back(s);
    h.push_back(0);
    for(int i = 1 ; i <= n ; ++i) {
        g.resize(pw[i]);
        p.resize(pw[i]);
        for(int now = 0 ; now < pw[i] ; ++now) {
            int cur = get(now,i-1);
            p[now] = h[cur];
            int d = now/pw[i-1]%3;
            for(auto v : f[cur]) {
                if(v == 'S') {
                    d = (d<<1)%3;
                    add(g[now],v);
                }
                else {
                    if(d == 2) {
                        d = 0;
                        add(g[now],v);
                    }
                    else d += 1;
                }
            }
            p[now] += d*pw[i-1];
        }
        h.clear();
        f.clear();
        swap(f,g);
        swap(h,p);
    }
    for(int i = 0 ; i < pw[n] ; ++i)
