      }
    }
    return pos;
  }

  void solveOne(istream &in, ostream &out) {
    in >> n;
    in >> t;
    size = 1;
    p3 = vector<int>(n + 1);
    p3[0] = 1;
    for (int i = 0; i < n; ++i) {
      size = size * 3;
      p3[i + 1] = size;
    }
    inverted = vector<bool>(t.size() + 1);
    for (int i = 0; i < t.size(); ++i) {
      if (t[i] == 'S') inverted[i + 1] = !inverted[i]; else inverted[i + 1] = inverted[i];
    }
    flip = vector<int>(size);
    for (int i = 0; i < size; ++i) {
      flip[i] = flip[i / 3] * 3;
      int r = i % 3;
      if (r == 1) flip[i] += 2; else if (r == 2) ++flip[i];
    }
    dest = vector<vector<pair<int, int>>>(t.size(), vector<pair<int, int>>((n + 1) * 3, pair<int, int>(-1, -1)));
    for (int i = 0; i < size; ++i) {
      int res = get(i, 0);
      if (inverted[t.size()]) res = flip[res];
      out << res << " ";
    }
    out << "\n";
  }

  void solve(std::istream &in, std::ostream &out) {
    int nt = 1;
    for (int it = 0; it < nt; ++it) {
      solveOne(in, out);
    }
  }
};


