void insert(int x) {
	int p=1;
	for(int i=1;i<=n;i++) {
		int c=x%3; x/=3;
		if(!trie[p][c]) trie[p][c]=++cnt;
		p=trie[p][c];
	}
	ed[p]=++tot;
}

void lazy(int x) {
	if(v[x]) {
		v[x]=0;
		int &y=trie[x][1],&z=trie[x][2];
		swap(y,z); 
		v[trie[x][0]]^=1; v[y]^=1; v[z]^=1;
	}
}

void add(int x) {
	if(ed[x]) return ;
	lazy(x);
	int a=trie[x][0],b=trie[x][1],c=trie[x][2];
	trie[x][0]=c; trie[x][1]=a; trie[x][2]=b;
	add(c);
}

