    {
        st.push_back(ch);
        if(st.size()>=2&&st[(int)st.size()-1]=='S'&&st[(int)st.size()-2]=='S') st.pop_back(),st.pop_back();
    }
    for(auto ch:st) ret+=ch;
    return ret;
}
void update_ans()
{
    int x=0,y=0;
    for(int i=n-1;i>=0;i--)
    {
        x=x*3+curdigit[i];
        y=y*3+curres[i];
    }
    ans[x]=y;
}
void solve(int cur,string t)
{
    if(cur==n) 
    {
        update_ans();
        return;
    }
