        temp*=3;
        zhishu[i]=temp;
    }
    const int num=temp;
    int ans[num];
    int sub[num];
    int c[num][12];
    for(int i=0;i<num;++i){
        ans[i]=i;
        sub[i]=0;
        for(int j=11;j>=12-n;--j){
            c[i][j]=0;
        }
    }
    for(int i=1;i<num;++i){
        for(int j=0;j<12;++j){
            c[i][j]=c[i-1][j];
        }
