        int j=11;
        while(c[i][j]>=3){
            c[i][j]-=3;
            j--;
            c[i][j]++;
        }
    }
    for(int i=0;i<num;++i){
        for(int j=012-n;j<12;++j){
            if(c[i][j]==2){
                sub[i]+=zhishu[11-j];
            }
        }
    }
    for(int i=0;i<t.size();++i){
        if(t[i]=='S'){
            for(int j=0;j<num;++j){
                temp=ans[j];
                ans[j]=2*temp-sub[temp];
