        salsa2[i] = salsa(i);
        //printf("salsa2[%d]=%d\n", i, salsa2[i]);
    }

    for(int n = 0; n < N; n ++){
        for(int j = 0; j < pow3[N]; j ++){
            D[n][j] = (j%pow3[n+1])/pow3[n];
            //printf("D[%d][%d]=%d\n", n, j, D[n][j]);
        }
        W[n][0] = pow3[n+1]-1;
    }


    memset(lastUpdated, 0, sizeof(lastUpdated));
    int cntS2 = 0;
    for(int n = 0; n < N; n ++){
