        for(int i = 0; i < M; i ++){
            if(T[i] == 'S'){
                W[n][i+1] = salsa2[W[n][i]];
            }else if(T[i] == 'R'){
                int temp = (W[n][i]-1);
                if(temp < 0){temp += pow3[n+1];}
                if(temp >= pow3[n+1]){temp -= pow3[n+1];}
                W[n][i+1] = temp;
            }

            //printf("W[%d][%d]=%d\n", n, i+1, W[n][i+1]);

            int indx0 = W[n][i];
