		go_finish[i] = i;
		cur_closest_zero[i] = 1;
	}

	for (int i = s.size() - 1; i >= 0; i--) {
		int code;
		if (s[i] == 'S') code = 1;
		else code = 0;

		for (int j = 0; j < SEVEN; ++j) {
			int be = go[j][code];
			if (be == 0 && s[i] == 'R') {
				spec[j] = 1;
			}
			else spec[j] = cur_closest_zero[j]+1;
		}

		for (int j = 0; j < SEVEN; ++j) cur_closest_zero[j] = spec[j];

		for (int j = 0; j < SEVEN; ++j) {
			int be = go[j][code];
			spec[j] = go_finish[be];
		}

		for (int j = 0; j < SEVEN; ++j) go_finish[j] = spec[j];
		closest_zero[i] = cur_closest_zero[0];
		go_zero[i] = go_finish[0];

