		if (C > left) {
			sht = go_finish[sht];
			left = 0;
			lng = get(lng, 0, s.size()-1);
		}

		else {
			sht = 0;
			left -= C;
			lng = get(lng, 0, C-1);
			lng = (go[lng][0]) % FIVE;
		}

		while (left) {
			int have = closest_zero[s.size() - left];
			if (have > left) {
				sht = go_zero[s.size() - left];
				left = 0;
				lng = get(lng, s.size()-left, s.size()-1);
			}
			else {
				sht = 0;
				lng = get(lng, s.size()-left, s.size()-(left-have)-1);
				left -= have;
				lng = (go[lng][0]) % FIVE;
			}
		}

		ans[i] = sht + SEVEN*lng;
	}

	int Q = 1;
