int salsa(int x){
    int temp = x;
    int pow3 = 1;
    int res = 0;
    for(int i = 0; i < N; i ++){
        int k = temp%3;
        temp /= 3;

        if(k == 1){
            res += pow3*2;
        }else if(k == 2){
            res += pow3;
        }
        pow3 *= 3;
    }
    return res;
}

