using namespace std;

vector<int> prepNext(int K) {
  int sz = 1;
  for (int i = 0; i < K; ++i) sz *= 3;

  vector<int> ret(sz);
  for (int m12 = 0; m12 < (1 << K); ++m12) {
    for (int m1 = m12;; m1 = (m1 - 1) & m12) {
      int x = 0, y = 0, pw = 1;
      for (int i = 0; i < K; ++i, pw *= 3) {
        if (m12 & (1 << i)) {
          if (m1 & (1 << i))
            x += pw, y += 2 * pw;
          else
            x += 2 * pw, y += pw;
        }
      }
      ret[x] = y;
      if (m1 == 0) break;
    }
  }
  return ret;
}

int main() {
  std::ios_base::sync_with_stdio(false);
  int N;
  string T;
  cin >> N >> T;

  int K = (N + 1) / 2;
  vector<int> next = prepNext(K);
  int H = next.size();

  int M = 1;
  for (int i = 0; i < N; ++i) M *= 3;

  vector<int> Plo(H);
  for (int i = 0; i < H; ++i) Plo[i] = i;

  vector<int> Phi(M), Shi(M);
  for (int i = 0; i < M; ++i) {
    Phi[i] = i / H;
    Shi[i] = 0;
  }

  int swapsCnt = 0;
  for (char c : T) {
    if (c == 'S') {
      for (auto& x : Plo) x = next[x];
      swapsCnt++;
    } else {
      int x = -1;
      for (int i = 0; i < H; ++i) {
        Plo[i]++;
        if (Plo[i] == H) {
          Plo[i] = 0;
          assert(x == -1);
          x = i;
        }
      }
      assert(x != -1);

      for (int y = 0; y * H + x < M; ++y) {
        int v = y * H + x;
        if (Shi[v] % 2 != swapsCnt % 2) {
          Phi[v] = next[Phi[v]];
          Shi[v] = swapsCnt;
        }
        Phi[v]++;
        if (Phi[v] * H == M) Phi[v] = 0;
      }
    }
  }

  for (int i = 0; i < M; ++i) {
    if (Shi[i] % 2 != swapsCnt % 2) {
      Phi[i] = next[Phi[i]];
    }
    int ans = Phi[i] * H + Plo[i % H];
    cout << ans << ' ';
  }
  cout << '\n';
  return 0;
}

