string norm(string s) {
    string t;
    for (char c : s) {
        if (c == 'R') {
            t.pb(c);
        } else {
            if (t.size() && t.back() == c) {
                t.pop_back();
            } else {
                t.pb(c);
            }
        }
    }
    return t;
}

int N;
ll ans[550000];
ll pre[15], cur[15];
ll p3[15];

void calc() {
    ll a = 0, b = 0;
    rep(i, N) {
        a += pre[i] * p3[i];
        b += cur[i] * p3[i];
    }
    ans[a] = b;
}

void solve(int p, string T) {
    if (p == N) {
        calc();
        return;
    }
    rep(c, 3) {
        pre[p] = c;
        string nx;
        int x = c;
        for (auto t : T) {
            if (t == 'S') {
                nx.pb(t);
                if (x != 0) {
                    x = 3 - x;
                }
            } else {
                ++x;
                if (x == 3) {
                    x = 0;
                    nx.pb(t);
                }
            }
        }
        cur[p] = x;
        solve(p + 1, norm(nx));
    }
}

