ll val(char c) 
{ 
    if (c >= '0' && c <= '9') 
        return (ll)c - '0'; 
    else
        return (ll)c - 'A' + 10; 
} 
char reVal(ll num) 
{ 
    if (num >= 0 && num <= 9) 
        return (char)(num + '0'); 
    else
        return (char)(num - 10 + 'A'); 
} 
string strev(string str) 
{ 
    int len = sz(str); 
    int i; 
    for (i = 0; i < len/2; i++) 
    { 
        char temp = str[i]; 
        str[i] = str[len-i-1]; 
        str[len-i-1] = temp; 
    } 
    return str;
}
string fromDeci(ll base, ll inputNum) 
{ 
    ll index = 0;
    string res="";
    while (inputNum > 0) 
    { 
        res+= reVal(inputNum % base); 
        inputNum /= base; 
    } 
    //res[index] = '\0'; 
    res=strev(res); 
    return res; 
} 
ll toDeci(string str, ll base) 
{ 
    ll len = sz(str); 
    ll power = 1; // Initialize power of base 
    ll num = 0;  // Initialize result 
    ll i; 
    for (i = len - 1; i >= 0; i--) 
    { 
        if (val(str[i]) >= base) 
        { 
           printf("Invalid Number"); 
           return -1; 
        } 
        num += val(str[i]) * power; 
        power = power * base; 
    } 

    return num; 
} 
