const int N = 1e6 + 10;

struct node {
    vector<node*> ch;
    int val;
    int push_swap;
    node() {
        val = -1;
        push_swap = 0;
    }
};

void build(node * root, vector<pii> a) {
    if (a.size() == 1) {
        root->val = a[0].second;
        return;
    }
    root->ch.resize(3);
    fori (z, 3) {
        vector<pii> cur;
        for (auto x : a) {
            if (x.first % 3 == z) {
                x.first /= 3;
                cur.push_back(x);
            }
        }
        root->ch[z] = new node();
        build(root->ch[z], cur);
    }
}

void push(node * root) {
    if (root->ch.empty())
        return;
    if (root->push_swap) {
        fori (z, 3) {
            if (!root->ch[z]->ch.empty()) {
                root->ch[z]->push_swap ^= 1;
                swap(root->ch[z]->ch[1], root->ch[z]->ch[2]);
            }
        }
        root->push_swap = 0;
    }
}

void upd_swap(node * root) {
    if (root->ch.empty())
        return;
    root->push_swap ^= 1;
    swap(root->ch[1], root->ch[2]);
}

void upd_inc(node * root) {
    if (root->ch.empty())
        return;

    push(root);

    node * tmp = root->ch[2];

    fori (i, 3) {
        swap(tmp, root->ch[i]);
    }

    upd_inc(root->ch[0]);

}

int ans[N];

void dfs(node * root, int cur, int lvl) {
    if (root->ch.empty()) {
//        watch(cur);
//        watch(root->val);
        ans[root->val] = cur;
        return;
    }
    fori (z, 3) {
        dfs(root->ch[z], cur + lvl * z, lvl * 3);
    }
}

