	for(int i=0;i<bin[n];i++)
		tr.insert(i);
	for(int i=1;i<=m;i++)
	{
		if(ch[i]=='S')tr.rev[1]^=1;
		else tr.addone(1,n);
	}
	tr.getans(1,0,0);
	for(int i=0;i<bin[n];i++)pr1(ans[i]);
