void push_down(int x){
	if(leaf[x]){laz[x]=0;return;}
	if(laz[x]){
		laz[x]=0;
		laz[son[x][0]]^=1;
		laz[son[x][1]]^=1;
		laz[son[x][2]]^=1;
		swap(son[x][1],son[x][2]);
	}
	return;
} 
void update(int x){
	if(leaf[x]) return;
	push_down(x);
	update(son[x][2]);
	int tmp=son[x][0];
	son[x][0]=son[x][2];
	son[x][2]=son[x][1];
	son[x][1]=tmp;
	return;
}
void build(int x,int cu,int level){
	if(level==n){leaf[x]=1;lid[x]=cu;return;}
	son[x][0]=++tot;build(son[x][0],cu+p3[level]*0,level+1);
	son[x][1]=++tot;build(son[x][1],cu+p3[level]*1,level+1);
	son[x][2]=++tot;build(son[x][2],cu+p3[level]*2,level+1);
	return;
}
