using namespace std;
const int maxn=15;
const int maxpos=531445;
int n,times[maxn],jump[maxpos],ans[maxpos];
vector<pair<int,bool> > change;
void init()
{
    times[0]=1;
    for(int i=1;i<=n;i++)
        times[i]=times[i-1]*3;
    return;
}
int main()
{
    ios::sync_with_stdio(0);
    cin.tie(0); cout.tie(0);
    cin>>n;
    init();
    string ss;
    cin>>ss;
    char last=ss[0];
    int num=0;
    for(int i=0;i<(int)ss.length();i++)
    {
        if(ss[i]!=last)
        {
            if(last=='S')
            {
                if(num%2)
                    change.emplace_back(1,true);
            }
            else change.emplace_back(num,false);
            last=ss[i]; num=1;
        }
        else num++;
    }
    if(last=='S')
    {
        if(num%2)
            change.emplace_back(1,true);
    }
    else change.emplace_back(num,false);
    for(int i=0;i<times[n];i++)
    {
        int cur=i,now=0;
        for(int j=n-1;j>=0;j--)
        {
            int val=cur/times[j];
            if(val==2) now+=times[j];
            if(val==1) now+=2*times[j];
            cur%=times[j];
        }
        jump[i]=now;
    }
    for(int i=0;i<times[n];i++)
    {
        int now=i;
        for(int j=0;j<(int)change.size();j++)
        {
            if(change[j].second)
                now=jump[now];
            else now=(now+change[j].first)%times[n];
        }
        ans[i]=now;
    }
    for(int i=0;i<times[n];i++)
        cout<<ans[i]<<(i==times[n]-1?'\n':' ');
    return 0;
