#include <bits/stdc++.h>
using namespace std;

const int MAXV = 6.1e5;
const int MAXN = 13;
int p3[MAXN];
int n;

struct node {
	union {
		int idx;
		node* c[3];
	};
	bool lazy;
};
node node_pool[MAXV * 3];
int node_idx = 0;

node* init(int cur, int pos) {
	node* v = &(node_pool[node_idx++]);
	if (cur == n) {
		// leaf
		v->idx = pos;
	} else {
		for (int x = 0; x < 3; x++) {
			v->c[x] = init(cur+1, pos + p3[cur] * x);
		}
	}
	return v;
}

void apply(node* v) {
	assert(v);
	v->lazy = !v->lazy;
	swap(v->c[1], v->c[2]);
}

void propagate(node* v) {
	if (!v->lazy) return;
	v->lazy = false;
	for (int x = 0; x < 3; x++) apply(v->c[x]);
}

void upd(node* v, int cur) {
	if (cur == n) {
		// leaf
	} else {
		propagate(v);
		swap(v->c[1], v->c[2]);
		swap(v->c[0], v->c[1]);
		upd(v->c[0], cur+1);
	}
}

int ans[MAXV];

void dfs(node* v, int cur, int pos) {
	if (cur == n) {
		ans[v->idx] = pos;
	} else {
		propagate(v);
		for (int x = 0; x < 3; x++) dfs(v->c[x], cur+1, pos + x * p3[cur]);
	}
}

int main() {
	ios::sync_with_stdio(0), cin.tie(0);
	string t;
	cin >> n >> t;
	p3[0] = 1;
	for (int i = 1; i <= n; i++) p3[i] = p3[i-1] * 3;
	node* tr = init(0, 0);
	for (char op : t) {
		if (op == 'R') {
			upd(tr, 0);
		} else if (op == 'S') {
			apply(tr);
		} else assert(false);
	}
	dfs(tr, 0, 0);
	for (int i = 0; i < p3[n]; i++) {
		cout << ans[i] << " \n"[i+1==p3[n]];
	}
}

