void chint(int &x){
    if(x==1) x=2;
    else if(x==2) x=1;
}

int salsa_step(int x){
    int res = 0;
    for (int i = 0; i < N; i++)
    {
        int y = (x%(int)pow(3,i+1))/(int)pow(3,i);
        chint(y);
        res += y*(int)pow(3,i);
    }
    return res;    
}

deque<int> salsa(deque<int> p){
    deque<int> ret(pow(3,N),0);
    for (int i = 0; i < pow(3,N); i++)
    {
        ret[i] = p[salsa_step(i)];
    }
    return ret;    
}

void rumba(deque<int> &p){
    int x = p.back();
    p.pop_back();
    p.push_front(x);
}

void shrink(string &t){
    for (int i = 0; i < t.length()-1; i++)
    {
        if(t[i]=='S' && t[i+1]=='S') {
            t = t.substr(0,i)+t.substr(i+2);
            i--;
            }
    }
}

