vector<int> degs(15);

pair<string, int> produce(int dig, pair<string, int> a, int depth)
{;
    vector<char> op;
    for (auto c: a.first)
    {
        if (c=='S')
        {
            if (dig==1) dig = 2;
            else if (dig==2) dig = 1;
            if (op.size()>0 && op.back()=='S') op.pop_back();
            else op.push_back('S');
        }
        else
        {
            if (dig==2)
            {
                dig = 0; op.push_back('R');
            }
            else dig++;
        }
    }
    string res;
    for (auto c: op) res+=c;
    return mp(res, dig*degs[depth] + a.second);
}


