    vector<pair<string, int>> a = {mp(s, 0)};

    for (int i = 0; i<N; i++)
    {
        vector<pair<string, int>> b(degs[i+1]);
        for (int j = 0; j<degs[i]; j++)
        {
            for (int dig = 0; dig<3; dig++)
            {
                b[dig*degs[i] + j] = produce(dig, a[j], i);
            }
        }
