const int SQM = 729;
const int M = SQM * SQM;
int nxt[2][M], nxt2[2][SQM], nxt3[2][SQM];
int p[M];
int q[M];
char s[M];
int type[M], cnt[M];
int U = 1, V = 1, W = 1, n, MID;

int lft[M], rgt[M];

int firstBAD[SQM + 10][SQM], val[SQM + 10][SQM];
void get(int l, int r){
	for(int i = 0; i < U; i++){
		firstBAD[r - l + 1][i] = r - l + 1;
		val[r - l + 1][i] = i;
	}
	for(int j = r; j >= l; j--){
		int t = type[j];
		for(int i = 0; i < U; i++){
			int new_node = nxt2[t][i];
			if(i != U - 1 || t != 1){
				val[j - l][i] = val[j - l + 1][new_node];
				firstBAD[j - l][i] = firstBAD[j - l + 1][new_node];
			} else{
				val[j - l][i] = i;
				firstBAD[j - l][i] = j - l;
			}
		};
	}

	for(int i = 0; i < V; i++){
		int L = lft[i], R = rgt[i], ind = 0;
		while(ind <= r - l){
			int nxt = firstBAD[ind][R];
			R = val[ind][R];
			L = (cnt[nxt - 1 + l] - cnt[ind - 1 + l]) & 1 ? nxt3[0][L] : L;
			if(nxt <= r - l){
				R = 0;
				L++;
				if(L == W) L = 0;
			}
			ind = nxt + 1;
		}
		q[i] = L * U + R;
	}
}

int transform(int v, int k){
	int pwr = 1, ret = 0;
	int V = v;
	for(int i = 0; i < k; i++, pwr *= 3, v /= 3){
		int t = (2 * v) % 3;
		ret += t * pwr;
	}
	return ret;
}
int h[M];
