	scanf("%s", s);
	// int m = 100000;
	int m = strlen(s);
	for(int i = 1; i <= m; i++){
		type[i] = rand() % 2;
		type[i] = s[i - 1] == 'R';
		cnt[i] = cnt[i - 1] + 1 - type[i];
	}
	MID = (n + 1) / 2;
	for(int i = 0; i < n; i++) V *= 3;
	for(int i = 0; i < MID; i++) U *= 3;
	for(int i = 0; i < n - MID; i++) W *= 3;

	for(int i = 0; i < V; i++){
		nxt[0][i] = transform(i, n);
		nxt[1][i] = (i + 1) % V;
	}

	for(int i = 0; i < U; i++){
		nxt2[0][i] = transform(i, MID);
		nxt2[1][i] = (i + 1) % U;
	}

	for(int i = 0; i < W; i++){
		nxt3[0][i] = transform(i, n - MID);
		nxt3[1][i] = (i + 1) % W;
	}

	for(int i = 0; i < V; i++){
		lft[i] = i / U;
		rgt[i] = i % U;
		p[i] = i;
	}

	for(int i = 1; i <= m; i += SQM){
		get(i, min(m + 1, i + SQM) - 1);
		for(int j = 0; j < V; j++) h[j] = q[p[j]];
		memcpy(p, h, sizeof p);
	}
	for(int i = 0; i < V; i++) printf("%d ", p[i]);
}

