int conv(int inp) {
  int cv=0;
  int t;

  for (t=1; inp>0; t*=3) {
    switch(inp % 3) {
    case 1: cv += 2*t; break;
    case 2: cv += t; break;
    }
    inp /= 3;
  }
  return cv;
}

