	for(int i=1;i<=n;i++) m*=3;
	for(int i=0;i<m;i++) O.dat[i]=i;
	for(int i=0;i<m;i++) rum[i]=(i+1)%m;
	for(int i=0;i<m;i++){
		ni=0;
		for(int j=0;j<n;j++){
			if(i%3==0) ni=(ni*3);
			if(i%3==1) ni=(ni*3)+2;
			if(i%3==2) ni=(ni*3)+1;
			i/=3;
		}
		nni=0;
		for(int j=0;j<n;j++){
			nni=(nni*3)+(ni%3);
