				cu--;
				while(cu){
					if(cu&1) O=mul(O,R);
					R=mul(R,R);
					cu>>=1;
				}
			}
			if(t[i-1]=='S'){
				for(int j=0;j<m;j++) S.dat[j]=sal[j];
				O=mul(O,S);
				cu--;
				while(cu){
					if(cu&1) O=mul(O,S);
					S=mul(S,S);
					cu>>=1;
				}
			}
			cu=0;
		}
