using namespace std;
struct mat{int dat[531441];}O,C,S,R;
int sal[531441],rum[531441];
int n,m,cu,ni,nni;
string t;
mat mul(mat a,mat b){
	for(int i=0;i<m;i++) C.dat[b.dat[i]]=a.dat[i];
	return C;
}
int main(){
	#ifdef FILIN
		#ifndef DavidDesktop
			freopen(FILIN,"r",stdin);
			freopen(FILOUT,"w",stdout);
		#endif
	#endif
	ios::sync_with_stdio(0);
	cin.tie(NULL);
	cout.tie(NULL);
	cin>>n>>t;
	m=1;
	for(int i=1;i<=n;i++) m*=3;
	for(int i=0;i<m;i++) O.dat[i]=i;
	for(int i=0;i<m;i++) rum[i]=(i+1)%m;
	for(int i=0;i<m;i++){
		ni=0;
		for(int j=0;j<n;j++){
			if(i%3==0) ni=(ni*3);
			if(i%3==1) ni=(ni*3)+2;
			if(i%3==2) ni=(ni*3)+1;
			i/=3;
		}
		nni=0;
		for(int j=0;j<n;j++){
			nni=(nni*3)+(ni%3);
			ni/=3;
		}
		sal[i]=nni;
	}
	cu=1;
	for(int i=1;i<t.size();i++){
		if(t[i]!=t[i-1]){
			if(t[i-1]=='R'){
				for(int i=0;i<m;i++) R.dat[i]=rum[i];
				O=mul(O,R);
				cu--;
				while(cu){
					if(cu&1) O=mul(O,R);
					R=mul(R,R);
					cu>>=1;
				}
			}
			if(t[i-1]=='S'){
				for(int i=0;i<m;i++) S.dat[i]=sal[i];
				O=mul(O,S);
				cu--;
				while(cu){
					if(cu&1) O=mul(O,S);
					S=mul(S,S);
					cu>>=1;
				}
			}
		}
	}
	for(int i=0;i<m;i++) cout<<O.dat[i]<<' ';
	cout<<endl;
	return 0;
}

