void reverse(){
    for(int i=0;i<pow;i++){
        for(int j=0;j<n;j++){
            if(cur[i][j]==1 or cur[i][j]==2) cur[i][j]=3-cur[i][j];
        }
    }
}
void plus(){
    for(int i=0;i<pow;i++){
        for(int j=0;j<n;j++){
            cur[i][j]+=1;
            if(cur[i][j]!=3) break;
            else{
                cur[i][j]=0;
            }
        }
    }
}
int num(int k){
    int ans=0;
    for(int i=n-1;i>=0;i--){
        ans*=3;
        ans+=cur[k][i];
    }
    return ans;
}
