int change3(int A){
  vector<int> b3(0);
  while(A>2){
    int aw3 = A%3;
    b3.push_back((aw3==0)?0:
                 (aw3==1)?2:1);
    A/=3;
  }
  int aw3 = A%3;
  b3.push_back((aw3==0)?0:
               (aw3==1)?2:1);
  int sum=0;
  for(int i=0;i<b3.size();i++){
   sum+= pow(3,i)*b3.at(i); 
  }
  return sum;
}


int main() {
