        if (c == 'S') {
            t[1].tag++;
        }
        else {
            pushdown(1);
            update(1, 0);
        }
    }
    int t = 1;
    for (int i = 1; i <= n; i++) t *= 3;
    for (int i = 0; i < t; i++) cout << query(1, i, 0) / 3 << ' ';
    cout << endl;
    return 0;
}

