void solve() {
	int i,j,k,l,r,x,y; string s;

	cin>>N>>T;
	p3[0]=1;
	FOR(i,N) p3[i+1]=p3[i]*3;

	for(i=1;i<=N;i++) {
		FOR(x,p3[i]) D[x]=x/p3[i-1];
		ZERO(F);
		int nf=0;
		FOR(x,T.size()) {
			y=W[i-1][x-1];
			if(T[x]=='S') {
				nf++;
				if((nf-F[y])%2) {
					FOR(j,3) {
						if(D[y+j*p3[i-1]]==1) D[y+j*p3[i-1]]=2;
						else if(D[y+j*p3[i-1]]==2) D[y+j*p3[i-1]]=1;
					}
					F[y]=nf;
				}
			}
			else {
				if((nf-F[y])%2) {
					FOR(j,3) {
						if(D[y+j*p3[i-1]]==1) D[y+j*p3[i-1]]=2;
						else if(D[y+j*p3[i-1]]==2) D[y+j*p3[i-1]]=1;
					}
					F[y]=nf;
				}
				FOR(j,3) {
					D[y+j*p3[i-1]]=(D[y+j*p3[i-1]]+1)%3;
				}
			}
			y=W[i-1][x];
			if((nf-F[y])%2) {
				FOR(j,3) {
					if(D[y+j*p3[i-1]]==1) D[y+j*p3[i-1]]=2;
					else if(D[y+j*p3[i-1]]==2) D[y+j*p3[i-1]]=1;
				}
				F[y]=nf;
			}
			FOR(j,3) if(D[y+j*p3[i-1]]==2) W[i][x]=W[i-1][x]+j*p3[i-1];
		}


		FOR(y,p3[i-1]) {
			if((nf-F[y])%2) {
				FOR(j,3) {
					if(D[y+j*p3[i-1]]==1) D[y+j*p3[i-1]]=2;
					else if(D[y+j*p3[i-1]]==2) D[y+j*p3[i-1]]=1;
				}
			}
		}
		FOR(x,p3[N]) P[x]+=D[x%p3[i]]*p3[i-1];
	}

	FOR(i,p3[N]) {
		cout<<P[i]<<" ";
	}
	cout<<endl;

}


