    for (int i = 0; i < sz; ++i) {
      for (int j = 0; j < 3; ++j) {
        ngo[i][j] = j;
      }
    }

    int sw = 0;

    auto make = [&] (int i) {
      for (int j = 0; j < 3; ++j) {
        if (ngo[i][j] == 0) {
          swap(ngo[i][(j + 1) % 3], ngo[i][(j + 2) % 3]);
        }
      }
    };    

    for (int i = 0; i < m; ++i) {
      if (s[i] == 'S') {
        sw ^= 1;
      } else {
