            who[i] = j * sz + id;
          }
        }
        for (int j = 0; j < 3; ++j) {
          ngo[id][j] = (ngo[id][j] + 1) % 3;
        } 
        if (sw) make(id);
      }
    }
    if (sw) for (int i = 0; i < sz; ++i) make(i);
    vector <int> cur(3 * sz);
    for (int i = 0; i < 3; ++i) {
      for (int j = 0; j < sz; ++j) {
        cur[i * sz + j] = go[j] + ngo[j][i] * p3[b];
      }
    }
    go = cur;
  }
  for (int i = 0; i < p3[n]; ++i) {
    cout << go[i] << ' ';
  }
  cout << '\n';
