ll k3(int x){
  if(x==0)return 1;
  static vector<ll> dp(50,-1);
  ll &it = dp[x];
  if(it>=0)return it;
  return it = 3 * k3(x-1);
}

class tree{
public:
  class node{
  public:
    int depth;
    int value;
    int turn;
    node* node0;
    node* node1;
    node* node2;
    node(int d,int b,int sum){
      turn = 0;
      depth = d;
      if(depth==b){
        value = sum;
        return;
      }
      node0 = new node(d+1,b,sum);
      node1 = new node(d+1,b,sum+k3(d));
      node2 = new node(d+1,b,sum+2*k3(d));
    }
  };
  node* root;
  int bottom;
  tree(int b){
    bottom = b;
    root = new node(0,bottom,0);
  }

  void turn(){
    ++(root->turn);
  }

  void add(){
    add(root);
  }

  void add(node* current){
    if(current->depth==bottom)return;
    if(current->turn&1){
      swap(current->node1,current->node2);
      ++(current->node0->turn);
      ++(current->node1->turn);
      ++(current->node2->turn);
    }
    current->turn = 0;
    swap(current->node2,current->node1);
    swap(current->node1,current->node0);
    add(current->node0);
  }

  vector<int> to_string(){
    vector<int> ans(k3(bottom));
    to_string(ans,root,0);
    return ans;
  }

  void to_string(vector<int>& res,node* current,int sum){
    if(current->depth==bottom){
      res[current->value] = sum;
      return;
    }
    if(current->turn&1){
      swap(current->node1,current->node2);
      ++(current->node0->turn);
      ++(current->node1->turn);
      ++(current->node2->turn);
    }
    current->turn = 0;
    to_string(res,current->node0,sum);
    to_string(res,current->node1,sum+k3(current->depth));
    to_string(res,current->node2,sum+2*k3(current->depth));
  }
};


int main(){
