  vector<int> line = inputs();
  tree t(n);
  foreach(i,line){
    if(i){
      t.turn();
    }else{
      t.add();
    }
  }
  vector<int> ans = t.to_string();
  foreach(i,ans){
    output(i);
  }
  outendl();
  return 0;
}

