void rever(int o){
	if(val[o]) return;
	swap(t[o][1],t[o][2]);
	tag[o]^=1;
}
void down(int o){
	if(tag[o]){
		rever(t[o][0]);
		rever(t[o][1]);
		rever(t[o][2]);
		tag[o]=0;
	}
}
void upd(int o){
	if(val[o]) return;
	down(o);
	swap(t[o][1],t[o][2]);
	swap(t[o][0],t[o][1]);
	upd(t[o][0]);
}
