struct node
{
	int val,s0,s1,s2,delta;	
}a[600000];

int n,ans[600000];
string s;

void pushdown(int pos)
{
	if(a[pos].delta==0) return;
	a[pos].delta=0;
	a[a[pos].s0].delta^=1;
	swap(a[a[pos].s0].s1,a[a[pos].s0].s2);
	a[a[pos].s1].delta^=1;
	swap(a[a[pos].s1].s1,a[a[pos].s1].s2);
	a[a[pos].s2].delta^=1;
	swap(a[a[pos].s2].s1,a[a[pos].s2].s2);
}

int build(int lev,int cur)
{
	int pos=++len;
	if(lev==n)
	{
		a[pos].val=cur;
		a[pos].s0=a[pos].s1=a[pos].s2=a[pos].delta=0;
		return pos;
	}
	a[pos].val=a[pos].delta=0;
	a[pos].s0=build(lev+1,cur);
	a[pos].s1=build(lev+1,cur+pow(3,lev));
	a[pos].s2=build(lev+1,cur+pow(3,lev)*2);
	return pos;
}

void Rot(int pos)
{
	if(pos==0) return;
	pushdown(pos);
	int tmp=a[pos].s0;
	a[pos].s0=a[pos].s2;
	a[pos].s2=a[pos].s1;
	a[pos].s1=tmp;
	Rot(a[pos].s0);
}

void Swp()
{
	a[1].delta^=1;
	swap(a[1].s1,a[1].s2);
}

void getans(int pos,int lev,int cur)
{
	if(lev==n)
	{
		ans[a[pos].val]=cur;
		return;
	}
	pushdown(pos);
	getans(a[pos].s0,lev+1,cur);
	getans(a[pos].s1,lev+1,cur+pow(3,lev));
	getans(a[pos].s2,lev+1,cur+pow(3,lev)*2);
}

