{
    scanf("%d",&n);
    scanf("%s",s+1);
    build(rt,n,1,0);
    for(int i=1;s[i];i++)
    {
        if(s[i]=='S') lz[rt]^=1;
        else solve(rt,n);
    }
    getans(rt,n,1,0);
    int sum=1;for(int i=1;i<=n;i++) sum*=3;
    for(int i=0;i<sum;i++) printf(i==sum-1?"%d\n":"%d ",ans[i]);
}

