void DFS(string &S){
    if(si(A)==N) return;

    int M=si(S);

    for(int i=0;i<3;i++){
        string T;
        int now=i;

        for(int j=0;j<M;j++){
            if(S[j]=='R'){
                now++;
                if(now==3){
                    now=0;
                    T+='R';
                }
            }else{
                if(j==M-1){
                    T+='S';
                    if(now) now=3-now;
                }
                else if(S[j+1]=='S') j++;
                else{
                    T+='S';
                    if(now) now=3-now;
                }
            }
        }

