	scanf("%d", &n);
	return n;
}

int main() {
	int n_ = ri();
	int n = n_;
	if (n & 1) n++;
	std::string s;
	std::cin >> s;
	int q = s.size();

	int half = n / 2;
	int k3 = 1;
	for (int i = 0; i < half; i++) k3 *= 3;
	int next[k3];
	for (int i = 0; i < k3; i++) {
		int cur[half];
		int t = i;
		for (int j = 0; j < half; j++) cur[j] = t % 3, t /= 3;
		for (auto &j : cur) {
			if (j == 1) j = 2;
			else if (j == 2) j = 1;
		}
		t = 0;
		for (int j = half; j--; ) t *= 3, t += cur[j];
		next[i] = t;
	}

	std::vector<int> plus[k3];
	int end[k3];
	for (int i = 0; i < k3; i++) {
		int cur = i;
		for (auto j : s) {
			if (j == 'S') {
				cur = next[cur];
				if (!plus[i].size()) plus[i].push_back(0);
				else if (plus[i].back() == 0) plus[i].pop_back();
				else plus[i].push_back(0);
			} else if (cur + 1 == k3) {
				cur = 0;
				if (!plus[i].size() || !plus[i].back()) plus[i].push_back(1);
				else plus[i].back()++;
			} else cur++;
		}
		end[i] = cur;
	}
	int res[k3 * k3];
	for (int i = 0; i < k3; i++) {
		for (int j = 0; j < k3; j++) {
			int cur = i;
			for (auto k : plus[j]) {
				if (!k) cur = next[cur];
				else cur = (cur + k) % k3;
			}
			res[i * k3 + j] = cur * k3 + end[j];
		}
	}
	int all = k3 * k3;
	if (n_ != n) all /= 3;
	for (int i = 0; i < all; i++) printf("%d ", res[i] % all);
	puts("");
	return 0;
}

