class people {
public:
	people() { n = 0; }
	~people() { if (n != 0)delete[]trip; }
	int pos;
	int n;
	int num;
	int* trip;
	void init(int _n, int _pos) {
		n = _n;
		num = pow(3, n);
		trip = new int[n + 1];
		pos = _pos;
		c10to3();
	}
	void c3to10() {
		int v = 0;
		for (int i = 0; i < n; i++) {
			v += pow(3, i) * trip[i];
		}
		pos = v % num;
	}
	void c10to3() {
		int v = pos;
		for (int i = 0; i < n; i++) {
			trip[i] = v % 3;
			v = floor((v - trip[i]) / 3);
		}
	}
	void rumba() {
		pos = (pos + 1) % num;
		c10to3();
	}
	void sarusa() {
		for (int i = 0; i < n; i++) {
			if (trip[i] == 1)trip[i] = 2;
			else if (trip[i] == 2)trip[i] = 1;
		}
		c3to10();
	}
};

int main() {
	int N, num;
	char T[200000];
	int* position;
	int* p_position;
	people* p;
	std::cin >> N;
	std::cin >> T;
	num = pow(3, N);
	position = new int[num + 1]();
	p_position = new int[num + 1]();
	p = new people[num + 1];
	for (int i = 0; i < num; i++) {
		p[i].init(N, i);
		p_position[i] = position[i] = i;
	}
	for (int i = 0; i < 200000; i++) {
		if (T[i] == 'S') {
			for (int j = 0; j < num; j++) {
				p[p_position[j]].sarusa();
				position[p[p_position[j]].pos] = p_position[j];
			}
		}
		else if (T[i] == 'R') {
			for (int j = 0; j < num; j++) {
				p[p_position[j]].rumba();
				position[p[p_position[j]].pos] = p_position[j];
			}
		}
		else break;

		memcpy(p_position, position, sizeof(int) * num);

	}
	for (int j = 0; j < num - 1; j++) {
		std::cout << p[j].pos << " ";
	}
	std::cout << p[num - 1].pos << std::endl;


	delete[]p;
	delete[]position;
	delete[]p_position;
	return 0;
}

